//５のプログラムpackage jp.co.dhw.review2;//６のプログラムpublic class Teacher extends User {	public String subject;		public Teacher (Integer id,String fName,String lName,String sub) {		super(id,fName,lName);		this.subject = sub;	}		public String getSubject() {		return this.subject;	}		@Override	public void showName(){		super.showName();		System.out.println("教科→" + this.subject);	}}